$input_text='test';Add-Type -AssemblyName System.Speech;$synth = New-Object System.Speech.Synthesis.SpeechSynthesizer;$synth.Rate = 0;$synth.Volume = 100;$format = New-Object System.Speech.AudioFormat.SpeechAudioFormatInfo(44100,[System.Speech.AudioFormat.AudioBitsPerSample]::Sixteen,[System.Speech.AudioFormat.AudioChannel]::Mono);$synth.SetOutputToWaveFile(".\$input_text.wav", $format);Write-Host "TTS $input_text";$synth.Speak($input_text);$synth.SetOutputToNull();$synth.Dispose();